mapper.insert:
INSERT INTO
infrMember (
	ifmmName
	, ifmmId
	, ifmmPwd
	, ifmmGenderCd
	, ifmmDob
	, ifmmDelNy
	, ifmmGradeCd
	, ifmmRegDate
	, ifmmStatusCd
	, ifmmEmailConsentNy
	, ifmmSmsConsentNy
	, ifmmPushConsentNy
) VALUES (
	#{ifmmName}
	, #{ifmmId}
	, #{ifmmPwd}
	, #{ifmmGenderCd}
	, #{ifmmDob}
	, 0
	, #{ifmmGradeCd}
	, curdate()
	, 78
	, #{ifmmEmailConsentNy}
	, #{ifmmSmsConsentNy}
	, #{ifmmPushConsentNy}
);


	SELECT last_insert_id()

INSERT INTO
infrMemberEmail (
	ifmmSeq
	, ifmeEmailFull
	, ifmeEmailAccount
	, ifmeEmailDomain
	, ifmeEmailDomainCd
	, ifmeDefaultNy
) VALUES (
	#{ifmmSeq}
	, concat(#{ifmeEmailAccount},'@',#{ifmeEmailDomain})
	, #{ifmeEmailAccount}
	, #{ifmeEmailDomain}
	, #{ifmeEmailDomainCd}		
	, 1
)

	INSERT INTO
	infrMemberAddress (
		ifmmSeq
		, ifmaZipCode
		, ifmaAddress1
		, ifmaAddress2
		, ifmaAddressReferences
        , ifmaDefaultNy
	) VALUES (
		#{ifmmSeq}
		, #{ifmaZipCode}
		, #{ifmaAddress1}
		, #{ifmaAddress2}
		, #{ifmaAddressReferences}
        , 1
	);

	INSERT INTO
	infrMemberPhone (
		ifmmSeq
        , ifmpNumber
        , ifmpDeviceCd
        , ifmpTelecomCd
        , ifmpDefaultNy
    ) VALUES (
		#{ifmmSeq}				
        , #{ifmpNumberMobile}		
        , 26
        , #{ifmpTelecomCd}
        , 1
    )

	INSERT INTO
	infrMemberPhone (
		ifmmSeq
        , ifmpNumber
        , ifmpDeviceCd
        , ifmpDefaultNy
    ) VALUES (
		#{ifmmSeq}
        , #{ifmpNumberHome}		
        , 25
        , 1
    )

	INSERT INTO 
	infrMemberNationality (
		ifmmSeq
        , ifnaSeq
        , ifmnDelNy
        , ifmnDefaultNy
    ) VALUES (
		#{ifmmSeq}
        , #{ifnaSeq}
        , 0
        , 1
    )

mapper.update:
UPDATE infrMember
SET
	ifmmName = #{ifmmName}
	, ifmmId = #{ifmmId}
	, ifmmPwd = #{ifmmPwd}
	, ifmmGenderCd = #{ifmmGenderCd}
	, ifmmDob = #{ifmmDob}
	, ifmmDelNy = 0
	, ifmmGradeCd = #{ifmmGradeCd}
	, ifmmStatusCd = 78
	, ifmmEmailConsentNy = #{ifmmEmailConsentNy}
	, ifmmSmsConsentNy = #{ifmmSmsConsentNy}
	, ifmmPushConsentNy = #{ifmmPushConsentNy}
WHERE
	1 = 1
AND
	ifmmSeq = #{ifmmSeq}

UPDATE infrMemberEmail
SET
	ifmeEmailFull = concat(#{ifmeEmailAccount},'@',#{ifmeEmailDomain})
	, ifmeEmailAccount = #{ifmeEmailAccount}
	, ifmeEmailDomain = #{ifmeEmailDomain}
	, ifmeEmailDomainCd = #{ifmeEmailDomainCd}
	, ifmeDefaultNy = 1
WHERE
	1 = 1
AND
	ifmmSeq = #{ifmmSeq}

UPDATE infrMemberAddress
SET
	ifmaZipCode = #{ifmaZipCode}
	, ifmaAddress1 = #{ifmaAddress1}
	, ifmaAddress2 = #{ifmaAddress2}
	, ifmaAddressReferences = #{ifmaAddressReferences}
	, ifmaDefaultNy = 1
WHERE
	1 = 1
AND
	ifmmSeq = #{ifmmSeq}

UPDATE infrMemberNationality
SET
	 ifnaSeq = #{ifnaSeq}
WHERE
	1 = 1
AND ifmmSeq = #{ifmmSeq}
